import { Component, Input } from '@angular/core';
import { CommonModule } from '@angular/common';
import { RouterModule } from '@angular/router';

@Component({
  selector: 'app-sidenav',
  standalone: true,
  imports: [
    CommonModule,
    RouterModule
  ],
  template: `
    <nav class="sidebar" [class.collapsed]="isCollapsed">
      <div class="sidebar-content">
        <div class="sidebar-header text-center mb-4">
          <div class="sidebar-brand">
            <i class="bi bi-geo-alt-fill text-primary"></i>
            <span class="brand-text" *ngIf="!isCollapsed">Taarifu</span>
          </div>
          <small class="text-muted" *ngIf="!isCollapsed">Administration</small>
        </div>
        
        <div class="sidebar-nav">
          <ul class="nav flex-column">
            <li class="nav-item">
              <a class="nav-link" routerLink="/dashboard" routerLinkActive="active" [title]="isCollapsed ? 'Dashboard' : ''">
                <i class="bi bi-speedometer2"></i>
                <span *ngIf="!isCollapsed">Dashboard</span>
              </a>
            </li>
            
            <li class="nav-item">
              <a class="nav-link" routerLink="/regions" routerLinkActive="active" [title]="isCollapsed ? 'Regions' : ''">
                <i class="bi bi-geo-alt"></i>
                <span *ngIf="!isCollapsed">Regions</span>
              </a>
            </li>
            
            <li class="nav-item">
              <a class="nav-link" routerLink="/districts" routerLinkActive="active" [title]="isCollapsed ? 'Districts' : ''">
                <i class="bi bi-building"></i>
                <span *ngIf="!isCollapsed">Districts</span>
              </a>
            </li>
          </ul>
          
          <hr class="my-3" *ngIf="!isCollapsed">
          
          <ul class="nav flex-column">
            <li class="nav-item">
              <a class="nav-link" href="#" (click)="logout()" [title]="isCollapsed ? 'Logout' : ''">
                <i class="bi bi-box-arrow-right"></i>
                <span *ngIf="!isCollapsed">Logout</span>
              </a>
            </li>
          </ul>
        </div>
      </div>
    </nav>
  `,
  styles: [`
    .sidebar {
      position: fixed;
      top: 56px;
      bottom: 0;
      left: 0;
      z-index: 100;
      width: 250px;
      background-color: #f8f9fa;
      border-right: 1px solid #dee2e6;
      transition: width 0.3s ease;
      overflow-x: hidden;
      overflow-y: auto;
    }

    .sidebar.collapsed {
      width: 60px;
    }

    .sidebar-content {
      padding: 1rem 0;
      height: 100%;
      display: flex;
      flex-direction: column;
    }

    .sidebar-header {
      padding: 0 1rem;
      border-bottom: 1px solid #dee2e6;
      padding-bottom: 1rem;
      margin-bottom: 1rem;
    }

    .sidebar-brand {
      display: flex;
      align-items: center;
      justify-content: center;
      gap: 0.5rem;
    }

    .sidebar-brand i {
      font-size: 1.5rem;
    }

    .brand-text {
      font-weight: 600;
      color: #007bff;
    }

    .sidebar-nav {
      flex: 1;
      padding: 0 0.5rem;
    }

    .sidebar .nav-link {
      display: flex;
      align-items: center;
      padding: 0.75rem 1rem;
      margin: 0.25rem 0;
      border-radius: 8px;
      font-weight: 500;
      color: #495057;
      text-decoration: none;
      transition: all 0.3s ease;
      white-space: nowrap;
    }

    .sidebar .nav-link i {
      font-size: 1.1rem;
      width: 20px;
      text-align: center;
      margin-right: 0.75rem;
    }

    .sidebar.collapsed .nav-link i {
      margin-right: 0;
    }

    .sidebar .nav-link:hover {
      color: #007bff;
      background-color: #e7f1ff;
      transform: translateX(2px);
    }

    .sidebar .nav-link.active {
      color: #fff;
      background-color: #007bff;
      box-shadow: 0 2px 4px rgba(0, 123, 255, 0.3);
    }

    .sidebar .nav-link.active:hover {
      background-color: #0056b3;
      transform: none;
    }

    .sidebar hr {
      border-color: #dee2e6;
      margin: 1rem 0;
    }

    @media (max-width: 768px) {
      .sidebar {
        transform: translateX(-100%);
        transition: transform 0.3s ease;
      }

      .sidebar.show {
        transform: translateX(0);
      }
    }
  `]
})
export class SidenavComponent {
  @Input() isCollapsed = false;

  logout() {
    console.log('Logout clicked');
    // Implement logout logic here
  }
}
