import { Component, OnInit, OnDestroy } from '@angular/core';
import { CommonModule } from '@angular/common';
import { ToastService, Toast } from '../../../core/services/toast.service';
import { Subscription } from 'rxjs';

@Component({
  selector: 'app-toast',
  standalone: true,
  imports: [CommonModule],
  template: `
    <div class="toast-container position-fixed top-50 start-50 translate-middle" style="z-index: 1055;">
      <div 
        *ngFor="let toast of toasts" 
        class="toast show" 
        role="alert" 
        aria-live="assertive" 
        aria-atomic="true"
        [class]="getToastClass(toast.type)">
        <div class="toast-header">
          <i [class]="getIconClass(toast.type)" class="me-2"></i>
          <strong class="me-auto">{{ toast.title }}</strong>
          <small class="text-muted">{{ getTimeAgo() }}</small>
          <button 
            type="button" 
            class="btn-close" 
            aria-label="Close"
            (click)="removeToast(toast.id)">
          </button>
        </div>
        <div class="toast-body">
          {{ toast.message }}
        </div>
      </div>
    </div>
  `,
  styles: [`
    .toast-container {
      pointer-events: none;
    }

    .toast {
      min-width: 350px;
      max-width: 500px;
      margin-bottom: 0.5rem;
      pointer-events: auto;
      box-shadow: 0 8px 32px rgba(0, 0, 0, 0.3);
      border-radius: 12px;
      border: none;
      animation: slideInFromTop 0.3s ease-out;
    }

    @keyframes slideInFromTop {
      from {
        opacity: 0;
        transform: translateY(-50px);
      }
      to {
        opacity: 1;
        transform: translateY(0);
      }
    }

    .toast-success {
      border-left: 4px solid #28a745;
    }

    .toast-error {
      border-left: 4px solid #dc3545;
    }

    .toast-warning {
      border-left: 4px solid #ffc107;
    }

    .toast-info {
      border-left: 4px solid #17a2b8;
    }

    .toast-header {
      background-color: #f8f9fa;
      border-bottom: 1px solid #dee2e6;
    }

    .toast-success .toast-header {
      background-color: #d4edda;
      color: #155724;
    }

    .toast-error .toast-header {
      background-color: #f8d7da;
      color: #721c24;
    }

    .toast-warning .toast-header {
      background-color: #fff3cd;
      color: #856404;
    }

    .toast-info .toast-header {
      background-color: #d1ecf1;
      color: #0c5460;
    }

    .toast-body {
      background-color: #ffffff;
    }

    .toast-success .toast-body {
      background-color: #d4edda;
      color: #155724;
    }

    .toast-error .toast-body {
      background-color: #f8d7da;
      color: #721c24;
    }

    .toast-warning .toast-body {
      background-color: #fff3cd;
      color: #856404;
    }

    .toast-info .toast-body {
      background-color: #d1ecf1;
      color: #0c5460;
    }

    .btn-close {
      background: none;
      border: none;
      font-size: 1.2rem;
      font-weight: bold;
      opacity: 0.5;
    }

    .btn-close:hover {
      opacity: 0.75;
    }
  `]
})
export class ToastComponent implements OnInit, OnDestroy {
  toasts: Toast[] = [];
  private subscription: Subscription = new Subscription();

  constructor(private toastService: ToastService) {}

  ngOnInit() {
    this.subscription = this.toastService.toasts$.subscribe(toasts => {
      this.toasts = toasts;
    });
  }

  ngOnDestroy() {
    this.subscription.unsubscribe();
  }

  getToastClass(type: Toast['type']): string {
    return `toast-${type}`;
  }

  getIconClass(type: Toast['type']): string {
    switch (type) {
      case 'success':
        return 'bi bi-check-circle-fill text-success';
      case 'error':
        return 'bi bi-x-circle-fill text-danger';
      case 'warning':
        return 'bi bi-exclamation-triangle-fill text-warning';
      case 'info':
        return 'bi bi-info-circle-fill text-info';
      default:
        return 'bi bi-info-circle-fill text-info';
    }
  }

  getTimeAgo(): string {
    return 'now';
  }

  removeToast(id: string): void {
    this.toastService.removeToast(id);
  }
}
