import { Component } from '@angular/core';
import { CommonModule } from '@angular/common';
import { RouterModule } from '@angular/router';

@Component({
  selector: 'app-sidenav',
  standalone: true,
  imports: [
    CommonModule,
    RouterModule
  ],
  template: `
    <nav class="col-md-3 col-lg-2 d-md-block bg-light sidebar collapse">
      <div class="position-sticky pt-3">
        <div class="sidebar-header text-center mb-4">
          <h5 class="text-primary">Taarifu Engine</h5>
          <small class="text-muted">Administration</small>
        </div>
        
        <ul class="nav flex-column">
          <li class="nav-item">
            <a class="nav-link" routerLink="/dashboard" routerLinkActive="active">
              <i class="bi bi-speedometer2 me-2"></i>
              Dashboard
            </a>
          </li>
          
          <li class="nav-item">
            <a class="nav-link" routerLink="/regions" routerLinkActive="active">
              <i class="bi bi-geo-alt me-2"></i>
              Regions
            </a>
          </li>
          
          <li class="nav-item">
            <a class="nav-link" routerLink="/districts" routerLinkActive="active">
              <i class="bi bi-building me-2"></i>
              Districts
            </a>
          </li>
        </ul>
        
        <hr class="my-3">
        
        <ul class="nav flex-column">
          <li class="nav-item">
            <a class="nav-link" href="#" (click)="logout()">
              <i class="bi bi-box-arrow-right me-2"></i>
              Logout
            </a>
          </li>
        </ul>
      </div>
    </nav>
  `,
  styles: [`
    .sidebar {
      position: fixed;
      top: 56px;
      bottom: 0;
      left: 0;
      z-index: 100;
      padding: 48px 0 0;
      box-shadow: inset -1px 0 0 rgba(0, 0, 0, .1);
      overflow-x: hidden;
      overflow-y: auto;
    }

    .sidebar .nav-link {
      font-weight: 500;
      color: #333;
    }

    .sidebar .nav-link:hover {
      color: #007bff;
      background-color: #f8f9fa;
    }

    .sidebar .nav-link.active {
      color: #007bff;
      background-color: #e7f1ff;
    }

    .sidebar-header {
      padding: 0 1rem;
    }
  `]
})
export class SidenavComponent {
  logout() {
    // This will be implemented when we add the auth service
    console.log('Logout clicked');
  }
}
